sys_script_include.script:
var APICEPBG = Class.create();
APICEPBG.prototype = {
    initialize: function() {
    },
	getCepInfo: function (cep){
		var obterInformacoesChuckNorris = new sn_ws.RESTMessageV2();

        obterInformacoesChuckNorris.setEndpoint("https://viacep.com.br/ws/" + cep + "/json");
        obterInformacoesChuckNorris.setHttpMethod("GET");
        var Response = obterInformacoesChuckNorris.execute();
		var Info = Response.getBody();
		Info = JSON.parse(Info);
		gs.info("Cidade:" + Info.localidade);
		gs.info("Rua:" + Info.logradouro);
		gs.info("Estado:" + Info.uf);
	},

    type: 'APICEPBG'
};
